mapper.select:
SELECT b_cp_code , cp_code , m_id as sell_id , b_cp_title , b_cp_contents , b_cp_price , cp_usedate , b_cp_bidder_number , b_cp_status , ac_status_code , b_cp_date , b_cp_date_bidding1 , b_cp_date_bidding2 , b_cp_date_decision1 , b_cp_date_decision2 , b_cp_groupcode , b_cp_re_count , b_cp_confirm , b_cp_confirm_status , b_cp_reject_reason , b_cp_cancel_reason , b_cp_cancel_date , b_recently FROM tb_bid_component WHERE ac_status_code IN (1,2,3) ORDER BY b_cp_date DESC
LIMIT #{start},#{end}
${searchKey} LIKE CONCAT('%',#{searchValue},'%') LIMIT #{start},#{end}

SELECT li.b_code, com.b_cp_title, com.b_cp_code, li.tr_type_name, li.b_date, com.b_cp_status, com.m_id as sell_id FROM tb_bid_component AS com INNER JOIN tb_bid_list AS li ON li.announced_code = com.b_cp_code
li.m_id = #{mId}
and ${searchKeyCp} LIKE CONCAT ('%',#{searchValueCp},'%')
ORDER BY li.b_date DESC
LIMIT #{bidComponentDTO.pagination.firstRecordIndex},#{bidComponentDTO.recordsPerPage}
