mapper.select:
SELECT b_code, announced_code, b_type_code, m_id, b_price, b_title, s_dep_rate, b_deposit, b_deposit_check, b_mo_code, b_deposit_date, tr_type_code, tr_type_name, dc_instructions, dc_application, dc_proposal, b_date, b_check, b_rank, b_deposit_available, b_deposit_refund, b_date_up, m_account_bank_name, m_account_number, m_payment_name FROM tb_bid_list
and m_id = #{id}
and announced_code = #{announceCode}
and b_code = #{bCode}
ORDER BY b_date desc LIMIT 1

mapper.update:
UPDATE tb_bid_list SET tr_type_code = #{updateStatus}, tr_type_name = #{updateStatusName}, b_date_up = NOW()
announced_code in
#{item}
AND tr_type_code = #{status}

UPDATE tb_board_seller
SET
	b_subject=#{bSubject},
	b_contents=#{bContents}
WHERE
	b_idx = #{bIdx}
	and m_id_buyer = #{mIdBuyer}
