mapper.update:
UPDATE 
	tb_business_plant
SET
	, bz_pl_name = #{bzPlName}
	, bz_pl_detail_addr = #{bzPlDetailAddr}
	, bz_pl_photo = #{bzPlPhoto}
	, bz_pl_power = #{bzPlPower}
	, bz_pl_area = #{bzPlArea}
	, bz_pl_inv_power = #{bzPlInvPower}
	, bz_pl_inv_count = #{bzPlInvCount}
	, bz_pl_inv_maker = #{bzPlInvMaker}
WHERE 
	bz_pl_code = #{bzPlCode};
