mapper.update:
  		  		UPDATE tb_trade_payment_out
				SET
					tr_payout_bank=#{accountBank},
					tr_payout_account=#{accountNumber}
				WHERE 
					tr_payout_check ='N' AND tr_payout_code = #{code}

UPDATE 
	tb_bid_list
SET
	b_rank=#{bRank}
WHERE 
	b_code= #{bCode}

  		  	UPDATE 
  		  		tb_bid_list
			SET
				b_check=#{bCheck}
			WHERE 
				b_code = #{bCode}

mapper.select:
SELECT pin.b_mo_date, p.tr_pr_code, p.announced_title, p.announced_code, p.m_id_buyer, p.tr_pr_price, sf_get_commission(p.tr_pr_price) AS comissionRate, p.tr_pr_price*sf_get_commission(p.tr_pr_price)/100 AS commission, p.tr_pr_price-(p.tr_pr_price*sf_get_commission(p.tr_pr_price)/100) AS availableOut, p.tr_payout_check FROM tb_trade_priority as p INNER JOIN tb_trade_payment_in AS pin on pin.announced_code = p.announced_code where p.tr_type_code = 14 AND p.tr_payout_check='N'
p.m_id_seller=#{mId}
and ${searchKey} LIKE CONCAT ('%',#{searchValue},'%')
LIMIT #{TradePriorityDTO.pagination.firstRecordIndex},#{TradePriorityDTO.recordsPerPage}

SELECT count(1) FROM tb_bid_component AS b inner JOIN tb_component AS c ON b.cp_code = c.cp_code
b.m_id = #{mId}
and ${searchKeyCp} LIKE CONCAT ('%',#{searchValueCp},'%')

SELECT c.cp_name, b.b_cp_code, b.cp_code, b.b_cp_title, b.b_cp_price, b.b_cp_status, b.b_cp_date_bidding1, b.b_cp_date_bidding2, b.b_cp_bidder_number FROM tb_bid_component AS b inner JOIN tb_component AS c ON b.cp_code = c.cp_code
b.m_id = #{mId}
and ${searchKeyCp} LIKE CONCAT ('%',#{searchValueCp},'%')
LIMIT #{bidComponentDTO.pagination.firstRecordIndex},#{bidComponentDTO.recordsPerPage}

SELECT count(1) FROM tb_bid_plant AS b left JOIN tb_business_plant AS p ON b.bz_pl_code = p.bz_pl_code
b.m_id = #{mId}
and ${searchKey} LIKE CONCAT ('%',#{searchValue},'%')

SELECT p.bz_pl_name, b.b_pl_code, b.b_pl_title, b.b_pl_price, b.b_pl_date_bidding1, b.b_pl_date_bidding2, b.b_pl_status, b.b_pl_number_of_bidder, b.b_pl_groupcode FROM tb_bid_plant AS b left JOIN tb_business_plant AS p ON b.bz_pl_code = p.bz_pl_code
b.m_id = #{mId}
and ${searchKey} LIKE CONCAT ('%',#{searchValue},'%')
ORDER BY b.b_pl_date_bidding1 desc , b.b_pl_status asc LIMIT #{bidPlantDTO.pagination.firstRecordIndex},#{bidPlantDTO.recordsPerPage}
